package org.sample;

public class Increment_Decrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 13;
		System.out.println(i--);
		System.out.println(i++);
		System.out.println(--i + i);
		System.out.println(++i);
		System.out.println(--i);
		System.out.println(i++ + --i + ++i);
		System.out.println(i--);
		System.out.println(i);
	}

}
